import React, { useState, useEffect } from 'react';
import './App.css';
import Game from './components/Game';

function App() {

  const [position, setPosition] = useState(0);
  const [goal, setGoal] = useState('');
  const [point, setPoint] = useState(0);
  const [previousPositions, setPreviousPositions] = useState([]);
  const [previousGoals, setPreviousGoals] = useState([]);
  let select_Player1 = document.querySelector(`.cell${position}`);
  let selectGoal = document.querySelector(`.cell${goal}`);


  useEffect(()=>{
    const randomNumber =  Math.floor(Math.random() * 70) + 1;;
    if(!goal){
      setGoal(randomNumber);
    }
    if(goal === position){
      localStorage.setItem('goal', randomNumber);
      localStorage.setItem('point', point+1);
      setGoal(randomNumber);
      setPoint(point => point+1);
    }
},[position]);



  if(select_Player1){
    select_Player1.innerHTML= `<div class="circular red">Player</div>`
  }

  if(selectGoal){
    selectGoal.innerHTML= `<div class="circular green">Goal</div>`;
  }

  const startGame =() => {
      const randomNumber =  Math.floor(Math.random() * 70) + 1
      const rememberPos = parseInt(localStorage.getItem('position'));
      const rememberGoal = parseInt(localStorage.getItem('goal'));
      const rememberPoint = parseInt(localStorage.getItem('point'));
      if(rememberPos && rememberPos <= 70){
        setPosition(parseInt(rememberPos));
      }else{
        setPosition(1);
      }

      if(rememberGoal && rememberGoal <= 70){
        setGoal(parseInt(rememberGoal));
      }else {
        setGoal(randomNumber);
      }

      if(rememberPoint){
        setPoint(parseInt(rememberPoint));
      }else {
        setPoint(0);
      }

  }

  const resetGame =() => {
    const randomNumber =  Math.floor(Math.random() * 70) + 1
    setPosition(1);
    setPoint(0);
    setGoal(randomNumber);
    localStorage.setItem('position', 1);
    localStorage.setItem('goal', randomNumber);
    localStorage.setItem('point', 0);
    if(select_Player1){
      select_Player1.innerHTML= `<div class="circular red">Player</div>`
    }

    if(selectGoal){
      selectGoal.innerHTML= `<div class="circular green">Goal</div>`;
    }

  }

  const onButtonClick=(newPosition) => {
    localStorage.setItem('position', newPosition);
    setPosition(newPosition);
    if(previousPositions.length < 5){
      setPreviousPositions([...previousPositions, newPosition]);
    }else if(previousPositions.length === 5){
      const arr = [...previousPositions].filter((el,i) => i !== 0);
      setPreviousPositions([...arr, newPosition]);
    }

    if(select_Player1){
      select_Player1.innerHTML= `<div class="circular"></div>`
    }
  }

  const onUndoClick =() => {
    // localStorage.setItem('position', previousPositions[previousPositions.length]);
    const arr = [...previousPositions].filter((el,i) => i === previousPositions.length);
    // setPosition();
    console.log(`position:${position}`);
    console.log(previousPositions);
    console.log(arr);
  }

console.log(localStorage.getItem('position'));
console.log(localStorage.getItem('goal'));
console.log(previousPositions);



  return (

    <div className="grid-container">
    <header className="header">
    <div> <button type="button" className="button_start" onClick={resetGame}>Reset</button></div>
        <div style={{color : "green", fontSize: "3rem", fontWeight: "bolder"}}>Player Navigation Game</div>
        <div> <button type="button" className="button_start" onClick={startGame}>
            {localStorage.getItem('position') ? 'Resume' : 'Start'}</button>
          </div>
    </header>

    <main className="main">

      <Game  onButtonClick={onButtonClick} position={position}/>
    </main>

    <footer className="footer">
    <div> <button type="button" className="button_start" onClick={onUndoClick}>undo</button></div>
      <div className="point"><div>Point: {point}</div></div>
    </footer>
</div>
  );
}

export default App;
