import React, { useState, useEffect } from 'react';
import './App.css';
import Game from './components/Game';

function App() {

  const [position, setPosition] = useState(0);
  const [goal, setGoal] = useState('');
  const [point, setPoint] = useState(0);


  useEffect(()=>{
    let randomNumber = Math.floor(Math.random() * 60) + 1;
    if(!goal){
      setGoal(randomNumber);
    }
    if(goal === position){
      setGoal(randomNumber);
      setPoint(point+1);
    }
},[position]);

  let select_Player1 = document.querySelector(`.cell${position}`);
  let selectGoal = document.querySelector(`.cell${goal}`);

  {select_Player1 && (select_Player1.innerHTML= `<div class="circular red">Player</div>`)};

  if(selectGoal){
    selectGoal.innerHTML= `<div class="circular green">Goal</div>`;
  }

  const startGame =() => {
    setPosition(1);
  }

  const resetGame =() => {
    setPosition(1);
    setGoal('');
  }

  const onButtonClick=(newPosition) => {
    // console.log(newPosition=== goal);
    setPosition(newPosition);
    // setGoal(goalFrom);

    {select_Player1 && (select_Player1.innerHTML= `<div> </div>`)};
  }
console.log(goal);
console.log(position);
console.log(goal === position);

  return (

    <div className="grid-container">
    <header className="header">
    <div> <button type="button" className="button_start" onClick={resetGame}>Reset</button></div>
        <div style={{color : "green", fontSize: "3rem", fontWeight: "bolder"}}>Player Navigation Game</div>
        <div> <button type="button" className="button_start" onClick={startGame}>Start</button></div>
    </header>

    <main className="main">

      <Game  onButtonClick={onButtonClick} position={position}/>
    </main>

    <footer className="footer">
    <div>Point: {point}</div>
    </footer>
</div>
  );
}

export default App;
