import React, { useState, useEffect } from 'react';

function Direction(props) {

    const [goal, setGoal] = useState('');

    let selectGoal = document.querySelector(`.cell${goal}`);

    useEffect(()=>{
        let randomNumber = Math.floor(Math.random() * 60) + 1;
        setGoal(randomNumber);
    },[]);

    // if(selectGoal){
    //     selectGoal.innerHTML= `<div class="circular"> </div>`;
    //   }


    const onLeftClick =() => {
        if(props.position > 1 && (props.position%10) !== 1){
            props.onButtonClick(props.position-1, goal);
        }
        
    }
    const onRightClick =() => {
        if(props.position <60 && (props.position%10) !== 0 ){
            props.onButtonClick(props.position+1,goal);
        }
        
    }
    const onUpClick =() => {
        if(props.position > 10){
            props.onButtonClick(props.position-10,goal);
        }
        
    }
    const onDownlick =() => {
        if(props.position < 51){
            props.onButtonClick(props.position+10,goal);
        }
    
    }

    console.log(goal);  
    console.log(props.position===goal);

    if(goal === props.position){
        let randomNumber = Math.floor(Math.random() * 60) + 1;
        setGoal(randomNumber);

        if(selectGoal){
            selectGoal.innerHTML= `<div> </div>`;
          }
    }
 
    return (
        <div>
            <button className="button_left" onClick={onLeftClick}>left</button>
            <button className="button_right" onClick={onRightClick}>right</button>
            <button className="button_up" onClick={onUpClick}>up</button>
            <button className="button_down" onClick={onDownlick}>down</button>
        </div>
    );
}

export default Direction;
